import React, { Component } from "react";
import {Row, Col, Container} from "react-bootstrap";
import Config from "../scripts/config";

class RobotState extends Component{
    state = {
        ros: null,
        x: 0.0,
        y: 0.0,
        theta: 0.0,
        linear_velocity: 0.0,
        angular_velocity: 0.0,
    };

    constructor(){
        super();
        this.init_connection();
    }


    init_connection(){
        this.state.ros = new window.ROSLIB.Ros();
        console.log(this.state.ros);

        this.state.ros.on("connection",()=>{
            console.log("connection established in Teleoperation Component!");
            console.log(this.state.ros);
            this.setState({connected:true});
        });
        this.state.ros.on("close",() =>{
            console.log("connection is closed! ");
            this.setState({connected: false});

            setTimeout(()=>{
                try{
                    this.state.ros.connect("ws://"+Config.ROSBRIDGE_SERVER_IP+":"+Config.ROSBRIDGE_SERVER_PORT+"");

                } catch(error){
                    console.log("connection problem");
                }


            }, Config.RECONNETION_TIMER);


        });

        try{
            this.state.ros.connect("ws://" + 
            Config.ROSBRIDGE_SERVER_IP +
            ":" +
            Config.ROSBRIDGE_SERVER_PORT+
            "");

        } catch(error){
            console.log("ws://" +
            Config.ROSBRIDGE_SERVER_IP +
            ":" +
            Config.ROSBRIDGE_SERVER_PORT+
            "")
            console.log("connection problem");
        }
    }


    componentDidMount(){
        this.getRobotState();
    }

    getRobotState(){
        var pose_sub = new window.ROSLIB.Topic({
            ros: this.state.ros, 
            name: "/turtle1/pose",
            messageType: "turtlesim/msg/Pose",


        });

        pose_sub.subscribe((message) => {
            this.setSate({x: message.x.Pose.toFixed(2)});
            this.setSate({y: message.y.Pose.toFixed(2)});
         });

    }



    render(){
        return(
            <div>
                <Row>
                    <Col>
                    <h4 className="mt-4">Position</h4>
                    <p className="mt-0">x: {this.state.x}</p>
                    <p className="mt-0">y: {this.state.y}</p>
                    <p className="mt-0">theta: {this.state.theta}</p>

                    </Col>
                </Row>
                <Row>
                    <Col>
                    <h4 className="mt-4">Velocities</h4>
                    <p className="mt-0">Linear Velocity: {this.state.linear_velocity}</p>
                    <p className="mt-0">Angular Velocity: {this.state.angular_velocity}</p>


                    </Col>
                </Row>

            </div>

        );
    }
}
export default RobotState;
